import { memo, useEffect, useMemo, useRef, useState } from 'react';
import {
  Avatar,
  Box,
  IconButton,
  LinearProgress,
  Typography
} from '@mui/material';
import SkipPreviousRoundedIcon from '@mui/icons-material/SkipPreviousRounded';
import SkipNextRoundedIcon from '@mui/icons-material/SkipNextRounded';
import FavoriteBorderRoundedIcon from '@mui/icons-material/FavoriteBorderRounded';

const Player = ({ track, queue = [], currentIndex = -1, onNavigateTrack }) => {
  const audioRef = useRef(null);
  const [progress, setProgress] = useState(0);

  const hasPrevious = useMemo(() => currentIndex > 0, [currentIndex]);
  const hasNext = useMemo(() => currentIndex >= 0 && currentIndex < queue.length - 1, [currentIndex, queue.length]);

  useEffect(() => {
    setProgress(0);
  }, [track?.song]);

  useEffect(() => {
    const audio = audioRef.current;
    if (!audio || !track?.song) {
      return;
    }
    audio.load();
    const playPromise = audio.play();
    if (playPromise?.catch) {
      playPromise.catch(() => {
        // autoplay might be blocked; user can press play manually
      });
    }
  }, [track?.song]);

  useEffect(() => {
    const audio = audioRef.current;
    if (!audio) return;

    const handleTimeUpdate = () => {
      if (!audio.duration || Number.isNaN(audio.duration)) {
        setProgress(0);
        return;
      }
      setProgress((audio.currentTime / audio.duration) * 100);
    };

    audio.addEventListener('timeupdate', handleTimeUpdate);
    audio.addEventListener('loadedmetadata', handleTimeUpdate);

    return () => {
      audio.removeEventListener('timeupdate', handleTimeUpdate);
      audio.removeEventListener('loadedmetadata', handleTimeUpdate);
    };
  }, [track?.song]);

  const handleEnded = () => {
    if (hasNext) {
      onNavigateTrack?.(currentIndex + 1);
    } else {
      const audio = audioRef.current;
      if (audio) {
        audio.pause();
        audio.currentTime = 0;
      }
      setProgress(0);
    }
  };

  const currentArtwork = track?.imgSrc;

  return (
    <Box
      component="footer"
      sx={{
        position: 'sticky',
        bottom: 0,
        width: '100%',
        alignSelf: 'end',
        zIndex: 1200,
        backdropFilter: 'blur(12px)',
        background: 'linear-gradient(180deg, rgba(18,18,18,0.6) 0%, rgba(12,12,12,0.95) 100%)',
        borderTop: '1px solid rgba(255,255,255,0.06)'
      }}
    >
      <LinearProgress
        variant="determinate"
        value={progress}
        sx={{
          height: 3,
          borderRadius: 99,
          backgroundColor: 'rgba(255,255,255,0.04)',
          '& .MuiLinearProgress-bar': {
            background: 'linear-gradient(90deg, rgba(29,185,84,0.65), rgba(34,197,94,0.85))'
          }
        }}
      />
      <Box
        sx={{
          display: 'flex',
          alignItems: 'center',
          gap: { xs: 1.5, md: 3 },
          px: { xs: 2, md: 3 },
          py: 2.5
        }}
      >
        <Box sx={{ display: 'flex', alignItems: 'center', gap: 2, minWidth: 0, flexGrow: { xs: 1, md: 0 } }}>
          <Avatar
            variant="rounded"
            src={currentArtwork || undefined}
            alt={track?.songName || 'cover'}
            sx={{ width: 56, height: 56, borderRadius: 2, bgcolor: 'rgba(255,255,255,0.04)' }}
          >
            {track?.songName?.[0]?.toUpperCase() || '♪'}
          </Avatar>
          <Box sx={{ minWidth: 0 }}>
            <Typography variant="subtitle1" fontWeight={600} noWrap>
              {track?.songName || 'Nothing playing'}
            </Typography>
            <Typography variant="body2" color="text.secondary" noWrap>
              {track ? track.artist || 'Unknown Artist' : 'Select a song to begin'}
            </Typography>
          </Box>
        </Box>

        <Box sx={{ flexGrow: 1, display: 'flex', alignItems: 'center', justifyContent: 'center' }}>
          <audio
            ref={audioRef}
            controls
            onEnded={handleEnded}
            style={{ width: '100%' }}
          >
            {track?.song && <source src={track.song} type="audio/mpeg" />}
            Your browser does not support the audio element.
          </audio>
        </Box>

        <Box sx={{ display: 'flex', alignItems: 'center', gap: 1.25 }}>
          <IconButton
            aria-label="Play previous song"
            onClick={() => hasPrevious && onNavigateTrack?.(currentIndex - 1)}
            disabled={!hasPrevious}
          >
            <SkipPreviousRoundedIcon />
          </IconButton>
          <IconButton
            aria-label="Play next song"
            onClick={() => hasNext && onNavigateTrack?.(currentIndex + 1)}
            disabled={!hasNext}
          >
            <SkipNextRoundedIcon />
          </IconButton>
          <IconButton aria-label="Toggle favourite">
            <FavoriteBorderRoundedIcon />
          </IconButton>
        </Box>
      </Box>
    </Box>
  );
};

export default memo(Player);
