import { useEffect, useState } from 'react';
import { Box, Card, CardContent, CircularProgress, Grid, Typography } from '@mui/material';
import api from '../../api';

const Dashboard = () => {
  const [stats, setStats] = useState({ songs: 0, categories: 0, admins: 1 });
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchStats = async () => {
      try {
        setLoading(true);
        const [{ data: songs }, { data: categories }] = await Promise.all([
          api.get('/songs'),
          api.get('/categories')
        ]);
        setStats({ songs: songs.length, categories: categories.length, admins: 1 });
      } catch (err) {
        setStats((prev) => ({ ...prev }));
      } finally {
        setLoading(false);
      }
    };

    fetchStats();
  }, []);

  if (loading) {
    return (
      <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '40vh' }}>
        <CircularProgress color="primary" />
      </Box>
    );
  }

  const statCards = [
    { label: 'Total Songs', value: stats.songs },
    { label: 'Categories', value: stats.categories },
    { label: 'Admin Users', value: stats.admins }
  ];

  return (
    <Box>
      <Typography variant="h4" gutterBottom>
        Overview
      </Typography>
      <Grid container spacing={3}>
        {statCards.map((card) => (
          <Grid item xs={12} sm={4} key={card.label}>
            <Card sx={{ backgroundColor: 'rgba(29,185,84,0.15)', borderRadius: 3 }}>
              <CardContent>
                <Typography variant="overline" color="text.secondary">
                  {card.label}
                </Typography>
                <Typography variant="h3" fontWeight={700} mt={1}>
                  {card.value}
                </Typography>
              </CardContent>
            </Card>
          </Grid>
        ))}
      </Grid>
    </Box>
  );
};

export default Dashboard;
