import {
  Avatar,
  IconButton,
  ListItem,
  ListItemAvatar,
  ListItemText,
  Tooltip
} from '@mui/material';
import PlayArrowIcon from '@mui/icons-material/PlayArrow';
import DeleteIcon from '@mui/icons-material/Delete';

const SongRow = ({ song, onPlay, onDelete, showDelete = false }) => {
  return (
    <ListItem
      divider
      secondaryAction={
        <>
          <Tooltip title="Play">
            <IconButton edge="end" sx={{ color: '#1db954' }} onClick={() => onPlay(song)}>
              <PlayArrowIcon />
            </IconButton>
          </Tooltip>
          {showDelete && (
            <Tooltip title="Delete">
              <IconButton edge="end" color="error" onClick={() => onDelete(song)}>
                <DeleteIcon />
              </IconButton>
            </Tooltip>
          )}
        </>
      }
    >
      <ListItemAvatar>
        <Avatar
          variant="rounded"
          src={song.imgSrc}
          alt={song.songName}
          sx={{ width: 56, height: 56, bgcolor: 'background.paper' }}
        >
          {song.songName?.[0]?.toUpperCase() || '♪'}
        </Avatar>
      </ListItemAvatar>
      <ListItemText
        primary={song.songName}
        secondary={`${song.artist || 'Unknown Artist'} • ${song.category || 'All'}`}
      />
    </ListItem>
  );
};

export default SongRow;
