import { useEffect, useState } from 'react';
import {
  Box,
  Card,
  CardContent,
  Grid,
  Skeleton,
  Stack,
  Typography
} from '@mui/material';
import LibraryMusicRoundedIcon from '@mui/icons-material/LibraryMusicRounded';
import CategoryRoundedIcon from '@mui/icons-material/CategoryRounded';
import ShieldRoundedIcon from '@mui/icons-material/ShieldRounded';
import api from '../../api';

const Dashboard = () => {
  const [stats, setStats] = useState({ songs: 0, categories: 0, admins: 1 });
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchStats = async () => {
      try {
        setLoading(true);
        const [{ data: songs }, { data: categories }] = await Promise.all([
          api.get('/songs'),
          api.get('/categories')
        ]);
        setStats({ songs: songs.length, categories: categories.length, admins: 1 });
      } catch (err) {
        setStats((prev) => ({ ...prev }));
      } finally {
        setLoading(false);
      }
    };

    fetchStats();
  }, []);

  const statCards = [
    {
      label: 'Total Songs',
      value: stats.songs,
      icon: <LibraryMusicRoundedIcon fontSize="large" />,
      gradient: 'linear-gradient(135deg, rgba(29,185,84,0.32), rgba(12,12,12,0.6))'
    },
    {
      label: 'Categories',
      value: stats.categories,
      icon: <CategoryRoundedIcon fontSize="large" />,
      gradient: 'linear-gradient(135deg, rgba(34,197,94,0.24), rgba(12,12,12,0.7))'
    },
    {
      label: 'Admin Users',
      value: stats.admins,
      icon: <ShieldRoundedIcon fontSize="large" />,
      gradient: 'linear-gradient(135deg, rgba(59,130,246,0.28), rgba(12,12,12,0.65))'
    }
  ];

  return (
    <Stack spacing={4}>
      <Box>
        <Typography variant="h4" gutterBottom>
          Overview
        </Typography>
        <Typography variant="body2" color="text.secondary">
          A quick snapshot of catalog health and activity.
        </Typography>
      </Box>

      <Grid container spacing={3}>
        {loading
          ? Array.from({ length: 3 }).map((_, idx) => (
              <Grid item xs={12} sm={4} key={`stat-skeleton-${idx}`}>
                <Skeleton
                  variant="rounded"
                  height={160}
                  sx={{ borderRadius: 4, bgcolor: 'rgba(255,255,255,0.05)' }}
                />
              </Grid>
            ))
          : statCards.map((card) => (
              <Grid item xs={12} sm={4} key={card.label}>
                <Card
                  sx={{
                    height: '100%',
                    borderRadius: 4,
                    background: card.gradient,
                    border: '1px solid rgba(255,255,255,0.06)',
                    boxShadow: '0 14px 32px rgba(0,0,0,0.35)',
                    transition: 'transform 200ms ease, box-shadow 200ms ease',
                    '&:hover': {
                      transform: 'translateY(-6px)',
                      boxShadow: '0 24px 48px rgba(0,0,0,0.4)'
                    }
                  }}
                >
                  <CardContent sx={{ display: 'flex', flexDirection: 'column', gap: 2 }}>
                    <Box sx={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between' }}>
                      <Typography variant="overline" color="rgba(255,255,255,0.7)" letterSpacing={2}>
                        {card.label}
                      </Typography>
                      <Box
                        sx={{
                          width: 42,
                          height: 42,
                          borderRadius: 2,
                          display: 'grid',
                          placeItems: 'center',
                          backgroundColor: 'rgba(255,255,255,0.08)'
                        }}
                      >
                        {card.icon}
                      </Box>
                    </Box>
                    <Typography variant="h3" fontWeight={700} sx={{ letterSpacing: '-0.02em' }}>
                      {card.value}
                    </Typography>
                  </CardContent>
                </Card>
              </Grid>
            ))}
      </Grid>
    </Stack>
  );
};

export default Dashboard;
