import { useEffect, useMemo, useState } from 'react';
import {
  Box,
  Button,
  Card,
  CardActionArea,
  CardContent,
  CardMedia,
  Grid,
  IconButton,
  List,
  Paper,
  Skeleton,
  Stack,
  Typography
} from '@mui/material';
import PlayArrowRoundedIcon from '@mui/icons-material/PlayArrowRounded';
import MusicNoteRoundedIcon from '@mui/icons-material/MusicNoteRounded';
import api from '../../api';
import SongRow from '../../components/SongRow';

const Home = ({ onSelectTrack }) => {
  const [songs, setSongs] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchSongs = async () => {
      try {
        setLoading(true);
        const { data } = await api.get('/songs');
        setSongs(data);
      } catch (err) {
        setError('Failed to load songs');
      } finally {
        setLoading(false);
      }
    };

    fetchSongs();
  }, []);

  const handlePlay = (track) => {
    if (!track) return;
    onSelectTrack?.(track, songs);
  };

  const featured = useMemo(() => songs.slice(0, 6), [songs]);
  const featuredSongWithCover = useMemo(
    () => songs.find((song) => Boolean(song?.imgSrc)),
    [songs]
  );

  if (loading) {
    return (
      <Stack spacing={4}>
        <Box
          sx={{
            minHeight: 220,
            borderRadius: 4,
            background: 'linear-gradient(35deg, rgba(34,197,94,0.18), rgba(15,15,15,0.4))',
            p: { xs: 3, md: 4 },
            display: 'flex',
            flexDirection: 'column',
            justifyContent: 'flex-end',
            gap: 2
          }}
        >
          <Skeleton variant="text" width={160} sx={{ fontSize: 28, bgcolor: 'rgba(255,255,255,0.08)' }} />
          <Skeleton variant="text" width={220} sx={{ fontSize: 18, bgcolor: 'rgba(255,255,255,0.08)' }} />
          <Skeleton variant="rounded" width={140} height={40} sx={{ bgcolor: 'rgba(255,255,255,0.08)' }} />
        </Box>

        <Grid container spacing={2.5}>
          {Array.from({ length: 6 }).map((_, idx) => (
            <Grid item xs={12} sm={6} md={4} lg={2} key={`featured-skeleton-${idx}`}>
              <Skeleton
                variant="rounded"
                height={220}
                sx={{ borderRadius: 4, bgcolor: 'rgba(255,255,255,0.06)' }}
              />
            </Grid>
          ))}
        </Grid>

        <Paper sx={{ p: 2.5 }}>
          <Stack spacing={1.5}>
            {Array.from({ length: 8 }).map((_, idx) => (
              <Skeleton
                key={`list-skeleton-${idx}`}
                variant="rounded"
                height={64}
                sx={{ borderRadius: 3, bgcolor: 'rgba(255,255,255,0.05)' }}
              />
            ))}
          </Stack>
        </Paper>
      </Stack>
    );
  }

  if (error) {
    return (
      <Typography color="error" variant="body1">
        {error}
      </Typography>
    );
  }

  return (
    <Stack spacing={5}>
      <Box
        sx={{
          position: 'relative',
          borderRadius: 4,
          overflow: 'hidden',
          background: 'linear-gradient(35deg, rgba(29,185,84,0.32) 0%, rgba(22,22,22,0.6) 55%, rgba(15,15,15,0.3) 100%)',
          border: '1px solid rgba(255,255,255,0.06)',
          boxShadow: '0 20px 40px rgba(0,0,0,0.35)'
        }}
      >
        {featuredSongWithCover?.imgSrc && (
          <Box
            component="img"
            src={featuredSongWithCover.imgSrc}
            alt={featuredSongWithCover.songName || 'cover'}
            loading="lazy"
            sx={{
              position: 'absolute',
              inset: 0,
              objectFit: 'cover',
              width: '100%',
              height: '100%',
              opacity: 0.2,
              filter: 'blur(24px) saturate(120%)'
            }}
          />
        )}
        <Box
          sx={{
            position: 'relative',
            display: 'flex',
            flexDirection: { xs: 'column', md: 'row' },
            alignItems: { md: 'flex-end' },
            justifyContent: 'space-between',
            gap: { xs: 3, md: 4 },
            p: { xs: 3, md: 5 }
          }}
        >
          <Box sx={{ maxWidth: 520 }}>
            <Typography variant="overline" color="rgba(255,255,255,0.7)" letterSpacing={2}>
              curated for you
            </Typography>
            <Typography variant="h3" sx={{ fontWeight: 700, mb: 1 }}>
              Good evening
            </Typography>
            <Typography variant="body1" color="text.secondary" sx={{ maxWidth: 420 }}>
              Set the tone with a blend of fresh drops and timeless favorites, tuned to the night.
            </Typography>
            <Button
              variant="contained"
              color="primary"
              sx={{ mt: 3, width: { xs: '100%', sm: 'auto' } }}
              onClick={() => featuredSongWithCover && handlePlay(featuredSongWithCover)}
            >
              Play something vibrant
            </Button>
          </Box>

          {featuredSongWithCover && (
            <Card
              sx={{
                width: { xs: '100%', sm: 260 },
                backgroundColor: 'rgba(15,15,15,0.75)',
                border: '1px solid rgba(255,255,255,0.08)',
                position: 'relative',
                overflow: 'hidden'
              }}
            >
              <CardActionArea
                onClick={() => handlePlay(featuredSongWithCover)}
                sx={{
                  '&:hover .hover-play, &:focus-visible .hover-play': {
                    opacity: 1,
                    transform: 'translateY(0)'
                  }
                }}
              >
                <Box sx={{ position: 'relative' }}>
                  <CardMedia
                    component="img"
                    image={featuredSongWithCover.imgSrc}
                    alt={featuredSongWithCover.songName || 'cover'}
                    sx={{ aspectRatio: '1 / 1', objectFit: 'cover' }}
                  />
                  <IconButton
                    aria-label="Play featured"
                    onClick={(event) => {
                      event.preventDefault();
                      handlePlay(featuredSongWithCover);
                    }}
                    sx={{
                      position: 'absolute',
                      bottom: 16,
                      right: 16,
                      bgcolor: 'primary.main',
                      color: '#0f0f0f',
                      opacity: 0,
                      transform: 'translateY(6px)',
                      transition: 'all 200ms ease',
                      '&:hover': { bgcolor: 'secondary.main' }
                    }}
                    className="hover-play"
                  >
                    <PlayArrowRoundedIcon />
                  </IconButton>
                </Box>
                <CardContent sx={{ display: 'flex', flexDirection: 'column', gap: 0.5 }}>
                  <Typography variant="subtitle1" fontWeight={600} noWrap>
                    {featuredSongWithCover.songName}
                  </Typography>
                  <Typography variant="body2" color="text.secondary" noWrap>
                    {featuredSongWithCover.artist || 'Unknown Artist'}
                  </Typography>
                </CardContent>
              </CardActionArea>
            </Card>
          )}
        </Box>
      </Box>

      {songs.length === 0 ? (
        <Paper
          elevation={0}
          sx={{
            p: { xs: 4, md: 6 },
            display: 'flex',
            flexDirection: { xs: 'column', md: 'row' },
            gap: 3,
            alignItems: 'center',
            justifyContent: 'space-between'
          }}
        >
          <Box
            sx={{
              width: 120,
              height: 120,
              borderRadius: '50%',
              background: 'radial-gradient(circle at center, rgba(34,197,94,0.35), transparent 65%)',
              display: 'grid',
              placeItems: 'center'
            }}
          >
            <MusicNoteRoundedIcon sx={{ fontSize: 48, color: 'rgba(255,255,255,0.65)' }} />
          </Box>
          <Box sx={{ maxWidth: 420 }}>
            <Typography variant="h5" gutterBottom>
              No songs yet — add some in Admin → Songs
            </Typography>
            <Typography variant="body2" color="text.secondary">
              Use the admin panel to upload tracks and they will appear here instantly for playback.
            </Typography>
          </Box>
        </Paper>
      ) : (
        <>
          <Box>
            <Typography variant="h5" gutterBottom>
              Fresh highlights
            </Typography>
            <Grid container spacing={2.5}>
              {featured.map((song) => (
                <Grid item xs={12} sm={6} md={4} lg={2} key={song._id || song.song}>
                  <Card
                    sx={{
                      position: 'relative',
                      overflow: 'hidden',
                      backgroundColor: 'rgba(21,21,21,0.95)'
                    }}
                  >
                    <CardActionArea
                      onClick={() => handlePlay(song)}
                      sx={{
                        position: 'relative',
                        '&:hover .hover-play, &:focus-visible .hover-play': {
                          opacity: 1,
                          transform: 'translateY(0)'
                        }
                      }}
                    >
                      <Box sx={{ position: 'relative' }}>
                        <CardMedia
                          component="img"
                          image={song.imgSrc}
                          alt={song.songName || 'cover'}
                          sx={{
                            aspectRatio: '1 / 1',
                            objectFit: 'cover',
                            bgcolor: 'rgba(255,255,255,0.03)'
                          }}
                        />
                        <IconButton
                          aria-label={`Play ${song.songName}`}
                          onClick={(event) => {
                            event.preventDefault();
                            handlePlay(song);
                          }}
                          sx={{
                            position: 'absolute',
                            bottom: 16,
                            right: 16,
                            bgcolor: 'rgba(15,15,15,0.75)',
                            color: 'primary.main',
                            opacity: 0,
                            transform: 'translateY(8px)',
                            transition: 'all 200ms ease',
                            '&:hover': {
                              bgcolor: 'primary.main',
                              color: '#101010'
                            }
                          }}
                          className="hover-play"
                        >
                          <PlayArrowRoundedIcon />
                        </IconButton>
                      </Box>
                      <CardContent sx={{ display: 'flex', flexDirection: 'column', gap: 0.5 }}>
                        <Typography variant="subtitle1" fontWeight={600} noWrap>
                          {song.songName}
                        </Typography>
                        <Typography variant="body2" color="text.secondary" noWrap>
                          {song.artist || 'Unknown Artist'}
                        </Typography>
                      </CardContent>
                    </CardActionArea>
                  </Card>
                </Grid>
              ))}
            </Grid>
          </Box>

          <Box>
            <Typography variant="h5" gutterBottom>
              All songs
            </Typography>
            <Paper sx={{ p: { xs: 1, sm: 2 } }}>
              <List sx={{ display: 'flex', flexDirection: 'column', gap: 1 }}>
                {songs.map((song) => (
                  <SongRow key={song._id || song.song} song={song} onPlay={handlePlay} />
                ))}
              </List>
            </Paper>
          </Box>
        </>
      )}
    </Stack>
  );
};

export default Home;
