import { useEffect, useState } from 'react';
import {
  Alert,
  Box,
  Button,
  Grid,
  List,
  Paper,
  Skeleton,
  Stack,
  TextField,
  Typography
} from '@mui/material';
import api from '../../api';
import SongRow from '../../components/SongRow';

const defaultForm = {
  songName: '',
  song: '',
  artist: '',
  category: '',
  imgSrc: ''
};

const SongsAdmin = ({ onSelectTrack }) => {
  const [songs, setSongs] = useState([]);
  const [form, setForm] = useState(defaultForm);
  const [loading, setLoading] = useState(true);
  const [feedback, setFeedback] = useState(null);
  const [saving, setSaving] = useState(false);

  const loadSongs = async () => {
    try {
      setLoading(true);
      const { data } = await api.get('/songs');
      setSongs(data);
      setFeedback((prev) => (prev?.type === 'error' ? null : prev));
    } catch (err) {
      setFeedback({ type: 'error', message: 'Failed to load songs' });
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    loadSongs();
  }, []);

  const handleChange = (event) => {
    const { name, value } = event.target;
    setForm((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    if (!form.songName || !form.song) {
      setFeedback({ type: 'error', message: 'Song name and MP3 URL are required.' });
      return;
    }

    try {
      setSaving(true);
      const payload = {
        songName: form.songName,
        song: form.song,
        artist: form.artist || undefined,
        category: form.category || undefined,
        imgSrc: form.imgSrc || undefined
      };
      const { data } = await api.post('/songs', payload);
      await loadSongs();
      setForm(defaultForm);
      setFeedback({ type: 'success', message: `Added "${data.songName}" to the catalog.` });
    } catch (err) {
      setFeedback({ type: 'error', message: 'Failed to create song.' });
    } finally {
      setSaving(false);
    }
  };

  const handleDelete = async (song) => {
    const confirmed = window.confirm(`Delete "${song.songName}"? This cannot be undone.`);
    if (!confirmed) return;
    try {
      await api.delete(`/songs/${song._id}`);
      setSongs((prev) => prev.filter((item) => item._id !== song._id));
      setFeedback({ type: 'success', message: `Deleted "${song.songName}".` });
    } catch (err) {
      setFeedback({ type: 'error', message: 'Failed to delete song.' });
    }
  };

  const handlePlay = (song) => {
    onSelectTrack?.(song, songs);
  };

  return (
    <Stack spacing={5}>
      <Paper
        sx={{
          p: { xs: 3, md: 4 },
          borderRadius: 4,
          background: 'linear-gradient(145deg, rgba(29,185,84,0.22), rgba(15,15,15,0.85))',
          border: '1px solid rgba(255,255,255,0.08)',
          boxShadow: '0 18px 36px rgba(0,0,0,0.35)'
        }}
      >
        <Stack spacing={2.5}>
          <Box>
            <Typography variant="h5" gutterBottom>
              Add a new song
            </Typography>
            <Typography variant="body2" color="text.secondary">
              Provide a direct MP3 URL and optional artwork to keep the catalog sharp.
            </Typography>
          </Box>

          {feedback && feedback.type === 'error' && (
            <Alert severity="error" onClose={() => setFeedback(null)}>
              {feedback.message}
            </Alert>
          )}
          {feedback && feedback.type === 'success' && (
            <Alert severity="success" onClose={() => setFeedback(null)}>
              {feedback.message}
            </Alert>
          )}

          <Box component="form" onSubmit={handleSubmit}>
            <Grid container spacing={2}>
              <Grid item xs={12} md={6}>
                <TextField
                  label="Song Name"
                  name="songName"
                  value={form.songName}
                  onChange={handleChange}
                  required
                  fullWidth
                />
              </Grid>
              <Grid item xs={12} md={6}>
                <TextField
                  label="Artist"
                  name="artist"
                  value={form.artist}
                  onChange={handleChange}
                  placeholder="Optional"
                  fullWidth
                />
              </Grid>
              <Grid item xs={12}>
                <TextField
                  label="MP3 URL"
                  name="song"
                  value={form.song}
                  onChange={handleChange}
                  required
                  fullWidth
                />
              </Grid>
              <Grid item xs={12} md={6}>
                <TextField
                  label="Category"
                  name="category"
                  value={form.category}
                  onChange={handleChange}
                  placeholder="Optional"
                  fullWidth
                />
              </Grid>
              <Grid item xs={12} md={6}>
                <TextField
                  label="Cover Image URL"
                  name="imgSrc"
                  value={form.imgSrc}
                  onChange={handleChange}
                  placeholder="Optional"
                  fullWidth
                />
              </Grid>
              <Grid item xs={12}>
                <Button type="submit" variant="contained" color="primary" disabled={saving}>
                  {saving ? 'Saving…' : 'Add song'}
                </Button>
              </Grid>
            </Grid>
          </Box>
        </Stack>
      </Paper>

      <Box>
        <Box sx={{ display: 'flex', alignItems: { xs: 'flex-start', md: 'center' }, justifyContent: 'space-between', flexDirection: { xs: 'column', md: 'row' }, gap: 2, mb: 2 }}>
          <Typography variant="h5">Current songs</Typography>
          <Typography variant="body2" color="text.secondary">
            {songs.length} total {songs.length === 1 ? 'track' : 'tracks'}
          </Typography>
        </Box>
        {loading ? (
          <Paper sx={{ p: 2, borderRadius: 4 }}>
            <Stack spacing={1.5}>
              {Array.from({ length: 6 }).map((_, idx) => (
                <Skeleton
                  key={`song-skeleton-${idx}`}
                  variant="rounded"
                  height={72}
                  sx={{ borderRadius: 3, bgcolor: 'rgba(255,255,255,0.05)' }}
                />
              ))}
            </Stack>
          </Paper>
        ) : songs.length ? (
          <Paper sx={{ p: { xs: 1, sm: 2 }, borderRadius: 4 }}>
            <List sx={{ display: 'flex', flexDirection: 'column', gap: 1 }}>
              {songs.map((song) => (
                <SongRow
                  key={song._id}
                  song={song}
                  onPlay={handlePlay}
                  onDelete={handleDelete}
                  showDelete
                />
              ))}
            </List>
          </Paper>
        ) : (
          <Paper
            sx={{
              p: { xs: 4, md: 6 },
              display: 'flex',
              flexDirection: 'column',
              alignItems: 'center',
              gap: 2,
              textAlign: 'center'
            }}
          >
            <Typography variant="h6">No songs yet</Typography>
            <Typography variant="body2" color="text.secondary">
              Use the form above to add your first track.
            </Typography>
          </Paper>
        )}
      </Box>
    </Stack>
  );
};

export default SongsAdmin;
