import { useEffect, useRef } from 'react';
import { Box, Typography } from '@mui/material';

const Player = ({ track }) => {
  const audioRef = useRef(null);

  useEffect(() => {
    if (track && audioRef.current) {
      audioRef.current.load();
      const playPromise = audioRef.current.play();
      if (playPromise && typeof playPromise.catch === 'function') {
        playPromise.catch(() => {
          // autoplay might be blocked; user can press play manually
        });
      }
    }
  }, [track]);

  return (
    <Box
      sx={{
        position: 'fixed',
        bottom: 0,
        left: 0,
        right: 0,
        backgroundColor: '#000000',
        borderTop: '1px solid rgba(255,255,255,0.08)',
        px: 3,
        py: 2,
        display: 'flex',
        alignItems: 'center',
        gap: 3,
        zIndex: 1200
      }}
    >
      <Box>
        <Typography variant="subtitle1" fontWeight={600}>
          {track ? track.songName : 'Nothing playing'}
        </Typography>
        <Typography variant="caption" color="text.secondary">
          {track ? track.artist || 'Unknown Artist' : 'Select a track to begin'}
        </Typography>
      </Box>
      <audio ref={audioRef} controls style={{ flex: 1 }}>
        {track && <source src={track.song} type="audio/mpeg" />}
        Your browser does not support the audio element.
      </audio>
    </Box>
  );
};

export default Player;
