import { useEffect, useState } from 'react';
import {
  Box,
  Card,
  CardActionArea,
  CardContent,
  CardMedia,
  CircularProgress,
  Grid,
  List,
  Typography
} from '@mui/material';
import api from '../../api';
import SongRow from '../../components/SongRow';

const Home = ({ onSelectTrack }) => {
  const [songs, setSongs] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchSongs = async () => {
      try {
        setLoading(true);
        const { data } = await api.get('/songs');
        setSongs(data);
      } catch (err) {
        setError('Failed to load songs');
      } finally {
        setLoading(false);
      }
    };

    fetchSongs();
  }, []);

  const featured = songs.slice(0, 4);

  if (loading) {
    return (
      <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '50vh' }}>
        <CircularProgress color="primary" />
      </Box>
    );
  }

  if (error) {
    return (
      <Typography color="error" variant="body1">
        {error}
      </Typography>
    );
  }

  if (!songs.length) {
    return (
      <Box>
        <Typography variant="h5" gutterBottom>
          No songs yet
        </Typography>
        <Typography variant="body2" color="text.secondary">
          Add tracks from the admin panel to start streaming.
        </Typography>
      </Box>
    );
  }

  return (
    <Box sx={{ display: 'flex', flexDirection: 'column', gap: 4 }}>
      <Box>
        <Typography variant="h4" gutterBottom>
          Featured
        </Typography>
        <Grid container spacing={3}>
          {featured.map((song) => (
            <Grid item xs={12} sm={6} md={3} key={song._id || song.song}>
              <Card
                sx={{
                  background: 'linear-gradient(135deg, rgba(29,185,84,0.25), rgba(0,0,0,0.7))',
                  borderRadius: 3
                }}
              >
                <CardActionArea onClick={() => onSelectTrack?.(song)}>
                  {song.imgSrc && (
                    <CardMedia component="img" image={song.imgSrc} height={180} alt={song.songName} />
                  )}
                  <CardContent>
                    <Typography variant="h6" gutterBottom>
                      {song.songName}
                    </Typography>
                    <Typography variant="body2" color="text.secondary">
                      {song.artist || 'Unknown Artist'}
                    </Typography>
                  </CardContent>
                </CardActionArea>
              </Card>
            </Grid>
          ))}
        </Grid>
      </Box>

      <Box>
        <Typography variant="h5" gutterBottom>
          All Songs
        </Typography>
        <List sx={{ bgcolor: 'background.paper', borderRadius: 2 }}>
          {songs.map((song) => (
            <SongRow key={song._id || song.song} song={song} onPlay={onSelectTrack} />
          ))}
        </List>
      </Box>
    </Box>
  );
};

export default Home;
