import { createTheme } from '@mui/material/styles';

const sharedRadius = 14;

const softShadows = [
  'none',
  '0px 3px 8px rgba(0,0,0,0.35)',
  '0px 6px 16px rgba(0,0,0,0.32)',
  '0px 10px 24px rgba(0,0,0,0.3)',
  '0px 14px 32px rgba(0,0,0,0.28)',
  '0px 20px 36px rgba(0,0,0,0.26)',
  ...Array(19).fill('0px 24px 48px rgba(0,0,0,0.28)')
];

const theme = createTheme({
  palette: {
    mode: 'dark',
    background: {
      default: '#0f0f0f',
      paper: '#161616'
    },
    primary: {
      main: '#1db954'
    },
    secondary: {
      main: '#22c55e'
    },
    text: {
      primary: 'rgba(255,255,255,0.94)',
      secondary: 'rgba(255,255,255,0.64)'
    }
  },
  shape: {
    borderRadius: sharedRadius
  },
  shadows: softShadows,
  typography: {
    fontFamily: '"Inter", "Roboto", "Helvetica", "Arial", sans-serif',
    fontSize: 15,
    body1: {
      fontSize: 15
    },
    body2: {
      fontSize: 14
    },
    h1: {
      letterSpacing: '-0.02em'
    },
    h2: {
      letterSpacing: '-0.02em'
    },
    h3: {
      letterSpacing: '-0.015em'
    },
    h4: {
      fontWeight: 600,
      letterSpacing: '-0.01em'
    },
    h5: {
      fontWeight: 600,
      letterSpacing: '-0.01em'
    },
    h6: {
      fontWeight: 500,
      letterSpacing: '-0.008em'
    }
  },
  transitions: {
    duration: {
      shortest: 150,
      shorter: 200,
      short: 250,
      standard: 250
    },
    easing: {
      easeInOut: 'cubic-bezier(0.4, 0, 0.2, 1)',
      easeOut: 'cubic-bezier(0, 0, 0.2, 1)',
      easeIn: 'cubic-bezier(0.4, 0, 1, 1)',
      sharp: 'cubic-bezier(0.4, 0, 0.6, 1)'
    }
  },
  components: {
    MuiCssBaseline: {
      styleOverrides: {
        body: {
          backgroundColor: '#0f0f0f'
        }
      }
    },
    MuiButton: {
      defaultProps: {
        disableElevation: true
      },
      styleOverrides: {
        root: ({ theme }) => ({
          borderRadius: sharedRadius,
          textTransform: 'none',
          paddingInline: theme.spacing(2.5),
          paddingBlock: theme.spacing(1.25),
          transition: 'all 250ms ease',
          position: 'relative',
          '&:focus-visible': {
            outline: '2px solid rgba(29,185,84,0.5)',
            outlineOffset: 2,
            boxShadow: '0 0 0 6px rgba(29,185,84,0.15)'
          }
        })
      }
    },
    MuiIconButton: {
      styleOverrides: {
        root: ({ theme }) => ({
          borderRadius: sharedRadius,
          transition: 'all 200ms ease',
          '&:hover': {
            backgroundColor: 'rgba(255,255,255,0.08)'
          },
          '&:focus-visible': {
            outline: '2px solid rgba(29,185,84,0.5)',
            outlineOffset: 2
          }
        })
      }
    },
    MuiCard: {
      defaultProps: {
        elevation: 0
      },
      styleOverrides: {
        root: {
          borderRadius: sharedRadius,
          backgroundImage: 'linear-gradient(145deg, rgba(255,255,255,0.04), rgba(0,0,0,0.3))',
          border: '1px solid rgba(255,255,255,0.04)',
          transition: 'transform 200ms ease, box-shadow 200ms ease, border-color 200ms ease',
          '&:hover': {
            transform: 'translateY(-2px) scale(1.01)',
            borderColor: 'rgba(29,185,84,0.3)',
            boxShadow: '0px 12px 28px rgba(0,0,0,0.35)'
          }
        }
      }
    },
    MuiPaper: {
      styleOverrides: {
        root: {
          borderRadius: sharedRadius,
          backgroundColor: '#161616',
          backgroundImage: 'none'
        }
      }
    },
    MuiListItemButton: {
      styleOverrides: {
        root: {
          borderRadius: sharedRadius,
          transition: 'all 200ms ease'
        }
      }
    }
  }
});

export default theme;
