import { useMemo } from 'react';
import { NavLink } from 'react-router-dom';
import {
  Avatar,
  Box,
  Divider,
  Drawer,
  IconButton,
  List,
  ListItemButton,
  ListItemIcon,
  ListItemText,
  Tooltip,
  Typography
} from '@mui/material';
import HomeRoundedIcon from '@mui/icons-material/HomeRounded';
import DashboardRoundedIcon from '@mui/icons-material/DashboardRounded';
import LibraryMusicRoundedIcon from '@mui/icons-material/LibraryMusicRounded';
import ChevronLeftRoundedIcon from '@mui/icons-material/ChevronLeftRounded';
import ChevronRightRoundedIcon from '@mui/icons-material/ChevronRightRounded';

const NAV_WIDTH_EXPANDED = 240;
const NAV_WIDTH_COLLAPSED = 72;

const navIconSx = (collapsed) => ({
  color: 'inherit',
  minWidth: collapsed ? 'auto' : 40,
  justifyContent: 'center'
});

const SidebarContent = ({ collapsed, onCollapseToggle, isDesktop, onNavigate }) => {
  const navItems = useMemo(
    () => [
      { label: 'Home', to: '/', icon: <HomeRoundedIcon fontSize="small" /> },
      { label: 'Admin Dashboard', to: '/admin', icon: <DashboardRoundedIcon fontSize="small" /> },
      { label: 'Manage Songs', to: '/admin/songs', icon: <LibraryMusicRoundedIcon fontSize="small" /> }
    ],
    []
  );

  return (
    <Box
      sx={{
        height: '100%',
        display: 'flex',
        flexDirection: 'column',
        bgcolor: 'rgba(12,12,12,0.75)',
        backdropFilter: 'blur(12px)',
        borderRight: '1px solid rgba(255,255,255,0.06)',
        px: collapsed ? 1 : 2,
        py: 3,
        gap: 3
      }}
    >
      <Box
        sx={{
          display: 'flex',
          alignItems: 'center',
          justifyContent: collapsed ? 'center' : 'flex-start',
          gap: 2,
          px: collapsed ? 0 : 1
        }}
      >
        <Box sx={{ position: 'relative' }}>
          <Avatar
            variant="circular"
            sx={{
              width: 40,
              height: 40,
              bgcolor: 'rgba(34,197,94,0.22)',
              color: 'primary.main',
              boxShadow: '0 10px 24px rgba(29,185,84,0.25)',
              backdropFilter: 'blur(8px)'
            }}
          >
            â™ª
          </Avatar>
          <Box
            sx={{
              position: 'absolute',
              bottom: 4,
              right: 4,
              width: 10,
              height: 10,
              borderRadius: '50%',
              bgcolor: 'primary.main',
              boxShadow: '0 0 0 2px rgba(15,15,15,0.8), 0 0 8px rgba(29,185,84,0.8)'
            }}
          />
        </Box>
        {!collapsed && (
          <Box>
            <Typography variant="subtitle1" fontWeight={600} sx={{ letterSpacing: '-0.01em' }}>
              Pulse
            </Typography>
            <Typography variant="caption" color="text.secondary">
              control center
            </Typography>
          </Box>
        )}
      </Box>

      <List disablePadding sx={{ display: 'flex', flexDirection: 'column', gap: 1 }}>
        {navItems.map((item) => {
          const button = (
            <ListItemButton
              key={item.to}
              component={NavLink}
              to={item.to}
              onClick={onNavigate}
              sx={{
                gap: collapsed ? 0 : 2,
                px: collapsed ? 1 : 2,
                py: 1.2,
                borderRadius: '999px',
                color: 'rgba(255,255,255,0.72)',
                transition: 'all 200ms ease',
                '&.active': {
                  color: 'primary.main',
                  backgroundColor: 'rgba(29,185,84,0.16)',
                  boxShadow: '0 8px 24px rgba(29,185,84,0.18)',
                  border: '1px solid rgba(29,185,84,0.35)'
                },
                '&:hover': {
                  backgroundColor: 'rgba(255,255,255,0.06)',
                  color: '#ffffff'
                }
              }}
            >
              <ListItemIcon sx={navIconSx(collapsed)}>{item.icon}</ListItemIcon>
              {!collapsed && (
                <ListItemText
                  primaryTypographyProps={{ fontWeight: 500, letterSpacing: '-0.01em' }}
                  primary={item.label}
                />
              )}
            </ListItemButton>
          );

          if (collapsed) {
            return (
              <Tooltip key={item.to} title={item.label} placement="right">
                {button}
              </Tooltip>
            );
          }

          return button;
        })}
      </List>

      {isDesktop && (
        <>
          <Box sx={{ flexGrow: 1 }} />
          <Divider sx={{ borderColor: 'rgba(255,255,255,0.06)' }} />
          <IconButton
            aria-label={collapsed ? 'Expand sidebar' : 'Collapse sidebar'}
            onClick={onCollapseToggle}
            sx={{
              alignSelf: collapsed ? 'center' : 'flex-end',
              mt: 2,
              color: 'rgba(255,255,255,0.7)',
              backgroundColor: 'rgba(255,255,255,0.04)',
              '&:hover': {
                backgroundColor: 'rgba(255,255,255,0.1)'
              }
            }}
          >
            {collapsed ? <ChevronRightRoundedIcon /> : <ChevronLeftRoundedIcon />}
          </IconButton>
        </>
      )}
    </Box>
  );
};

const Sidebar = ({ open, onClose, collapsed, onCollapseToggle, isDesktop }) => {
  if (!isDesktop) {
    return (
      <Drawer
        anchor="left"
        open={open}
        onClose={onClose}
        ModalProps={{ keepMounted: true }}
        PaperProps={{
          sx: {
            width: NAV_WIDTH_EXPANDED,
            borderRight: '1px solid rgba(255,255,255,0.06)',
            backgroundColor: 'rgba(12,12,12,0.92)',
            backdropFilter: 'blur(14px)'
          }
        }}
      >
        <SidebarContent
          collapsed={false}
          onCollapseToggle={onCollapseToggle}
          isDesktop={false}
          onNavigate={onClose}
        />
      </Drawer>
    );
  }

  return (
    <Box
      component="aside"
      sx={{
        width: collapsed ? NAV_WIDTH_COLLAPSED : NAV_WIDTH_EXPANDED,
        transition: 'width 240ms ease',
        position: 'sticky',
        top: 0,
        alignSelf: 'start',
        height: '100vh',
        zIndex: 1100
      }}
    >
      <SidebarContent
        collapsed={collapsed}
        onCollapseToggle={onCollapseToggle}
        isDesktop
        onNavigate={() => {}}
      />
    </Box>
  );
};

export const sidebarWidths = {
  expanded: NAV_WIDTH_EXPANDED,
  collapsed: NAV_WIDTH_COLLAPSED
};

export default Sidebar;
